实现相对于上次的实现，我们引入了更多的文件：
.
|-- buff.go         // 缓冲channel相关实现
|-- db.go           // kv主体实现
|-- db_test.go      // 测试文件
|-- dirty.go        // dirtyMap与实际存储的entity的实现
|-- error.go        // 自定义错误
`-- operator.go     // 具体操作kv的实现

buff.gopackage kv

type typeRequest int

const (
	getValue typeRequest = iota
	setValue
	deleteKey
	rangeDB
)

type typeResponse int

const (
	withError typeResponse = iota
	withValue
)

type requestFields int

const (
	requestKey requestFields = iota
	requestValue
	requestTimeout
	requestFunc
)

type responseFields int

const (
	responseKey responseFields = iota
	responseValue
	responseTimeout
	responseError
)

type dbRequest struct &#123;
	requestType typeRequest
	args        map[requestFields]interface&#123;&#125;
	callback    chan dbResponse
&#125;

type dbResponse struct &#123;
	responseType typeResponse
	returns      map[responseFields]interface&#123;&#125;
&#125;

func newDbRequest(rType typeRequest) *dbRequest &#123;
	return &amp;dbRequest&#123;
		requestType: rType,
		args:        make(map[requestFields]interface&#123;&#125;),
		callback:    make(chan dbResponse, 1),
	&#125;
&#125;

db.gopackage kv

import (
	"sync"
	"time"
)

type IDatabase interface &#123;
	Get(key interface&#123;&#125;) (value interface&#123;&#125;, timeout time.Duration, err error)
	Set(key interface&#123;&#125;, value interface&#123;&#125;, timeout time.Duration) (v interface&#123;&#125;, t time.Duration, e error)
	Delete(key interface&#123;&#125;) (ok bool, err error)
	Range(f func(key, value interface&#123;&#125;))
	Close()
&#125;

func NewDatabase(workers, buffSize, dirtyCount uint, hash func(key interface&#123;&#125;) uint) IDatabase &#123;
	// init kv-database
	db := &amp;database&#123;
		buff:    make(chan *dbRequest, buffSize),
		exit:    make(chan struct&#123;&#125;, 1),
		data:    make(map[uint]*dirtyMap, dirtyCount),
		size:    dirtyCount,
		workers: workers,
		hash:    hash,
	&#125;

	// init dirty-map
	for i := uint(0); i &lt; dirtyCount; i++ &#123;
		db.data[i] = &amp;dirtyMap&#123;
			lock:  sync.RWMutex&#123;&#125;,
			dirty: make(map[interface&#123;&#125;]*entity),
		&#125;
	&#125;

	// start up operators
	db.operator()
	return db
&#125;

type database struct &#123;
	size    uint
	workers uint
	buff    chan *dbRequest
	exit    chan struct&#123;&#125;
	data    map[uint]*dirtyMap
	hash    func(interface&#123;&#125;) uint
&#125;

func (d *database) Get(key interface&#123;&#125;) (value interface&#123;&#125;, timeout time.Duration, err error) &#123;
	req := newDbRequest(getValue)
	defer close(req.callback)
	req.args = map[requestFields]interface&#123;&#125;&#123;
		requestKey: key,
	&#125;
	d.buff &lt;- req

	resp := &lt;-req.callback
	switch resp.responseType &#123;
	case withError:
		err := resp.returns[responseError].(error)
		return nil, time.Duration(0), err
	default:
		return resp.returns[responseValue], resp.returns[responseTimeout].(time.Duration), nil
	&#125;
&#125;

func (d *database) Set(key interface&#123;&#125;, value interface&#123;&#125;, timeout time.Duration) (v interface&#123;&#125;, t time.Duration, e error) &#123;
	req := newDbRequest(setValue)
	defer close(req.callback)
	req.args = map[requestFields]interface&#123;&#125;&#123;
		requestKey:     key,
		requestValue:   value,
		requestTimeout: timeout,
	&#125;
	d.buff &lt;- req

	resp := &lt;-req.callback
	switch resp.responseType &#123;
	case withError:
		err := resp.returns[responseError].(error)
		return nil, time.Duration(0), err
	default:
		return resp.returns[responseValue], resp.returns[responseTimeout].(time.Duration), nil
	&#125;
&#125;

func (d *database) Delete(key interface&#123;&#125;) (ok bool, err error) &#123;
	req := newDbRequest(deleteKey)
	defer close(req.callback)
	req.args = map[requestFields]interface&#123;&#125;&#123;
		requestKey: key,
	&#125;
	d.buff &lt;- req

	resp := &lt;-req.callback
	switch resp.responseType &#123;
	case withError:
		err := resp.returns[responseError].(error)
		return false, err
	default:
		return true, nil
	&#125;
&#125;

func (d *database) Range(f func(key interface&#123;&#125;, value interface&#123;&#125;)) &#123;
	req := newDbRequest(rangeDB)
	defer close(req.callback)
	req.args = map[requestFields]interface&#123;&#125;&#123;
		requestFunc: f,
	&#125;
	d.buff &lt;- req

	&lt;-req.callback
	return
&#125;

func (d *database) Close() &#123;
	d.exit &lt;- struct&#123;&#125;&#123;&#125;
&#125;

dirty.gopackage kv

import (
	"sync"
	"time"
)

type entity struct &#123;
	timeout time.Time
	data    interface&#123;&#125;
&#125;

type dirtyMap struct &#123;
	lock  sync.RWMutex
	dirty map[interface&#123;&#125;]*entity
&#125;

error.gopackage kv

import "fmt"

type outOfRangeError struct &#123;
	upperIndex int
	lowerIndex int
&#125;

func (o outOfRangeError) Error() string &#123;
	return fmt.Sprintf("kv-db out of range with [%v:%v]", o.lowerIndex, o.upperIndex)
&#125;

type notFoundError struct &#123;
	key interface&#123;&#125;
&#125;

func (n notFoundError) Error() string &#123;
	return fmt.Sprintf("kv-db cannot find key: %#v", n.key)
&#125;

operator.gopackage kv

import (
	"time"
)

func (d *database) operator() &#123;
	// start up exit listener
	exitChan := make(chan struct&#123;&#125;, d.workers)
	go listenExitChan(d.exit, exitChan, d.workers)

	// start up operators
	for i := uint(0); i &lt; d.workers; i++ &#123;
		go startupOperator(d, exitChan)
	&#125;
&#125;

func listenExitChan(from, to chan struct&#123;&#125;, workers uint) &#123;
	select &#123;
	case &lt;-from:
		for i := uint(0); i &lt; workers; i++ &#123;
			to &lt;- struct&#123;&#125;&#123;&#125;
		&#125;
	&#125;
&#125;

func startupOperator(d *database, e chan struct&#123;&#125;) &#123;
	for &#123;
		select &#123;
		case request := &lt;-d.buff:
			switch request.requestType &#123;
			case getValue:
				request.callback &lt;- getValueOperate(d, request.args[requestKey])
			case setValue:
				request.callback &lt;- setValueOperate(d, request.args[requestKey], request.args[requestValue], request.args[requestTimeout].(time.Duration))
			case deleteKey:
				request.callback &lt;- deleteKeyOperate(d, request.args[requestKey])
			case rangeDB:
				request.callback &lt;- rangeDbOperate(d, request.args[requestFunc].(func(interface&#123;&#125;, interface&#123;&#125;)))
			&#125;
		case &lt;-e:
			return
		&#125;
	&#125;
&#125;

func getValueOperate(d *database, key interface&#123;&#125;) (resp dbResponse) &#123;
	// get dirty-map index and set read-lock
	index := d.hash(key) % d.size
	_map := d.data[index]
	_map.lock.RLock()
	defer _map.lock.RUnlock()

	if v, ok := _map.dirty[key]; !ok &#123;
		// condition: cannot find key in dirty-map
		// return error
		resp = dbResponse&#123;
			responseType: withError,
			returns: map[responseFields]interface&#123;&#125;&#123;
				responseError: notFoundError&#123;key: key&#125;,
			&#125;,
		&#125;
	&#125; else &#123;
		// condition: get entity succeed in dirty-map
		if v.timeout.Before(time.Now()) &#123;
			// condition: key-value pair timeout
			// delete key-value pair
			_map.lock.Lock()
			defer _map.lock.Unlock()
			delete(_map.dirty, key)

			// return error
			resp = dbResponse&#123;
				responseType: withError,
				returns: map[responseFields]interface&#123;&#125;&#123;
					responseError: notFoundError&#123;key: key&#125;,
				&#125;,
			&#125;
		&#125; else &#123;
			// condition: key-value pair not timeout
			// return value
			resp = dbResponse&#123;
				responseType: withValue,
				returns: map[responseFields]interface&#123;&#125;&#123;
					responseValue:   v.data,
					responseTimeout: v.timeout.Sub(time.Now()),
				&#125;,
			&#125;
		&#125;
	&#125;

	return resp
&#125;

func setValueOperate(d *database, key, value interface&#123;&#125;, timeout time.Duration) (resp dbResponse) &#123;
	// get dirty-map index and set write-lock
	index := d.hash(key) % d.size
	_map := d.data[index]
	_map.lock.Lock()
	defer _map.lock.Unlock()

	// update dirty-map
	v := &amp;entity&#123;
		timeout: time.Now().Add(timeout),
		data:    value,
	&#125;
	_map.dirty[key] = v

	// return value
	resp = dbResponse&#123;
		responseType: withValue,
		returns: map[responseFields]interface&#123;&#125;&#123;
			responseValue:   v.data,
			responseTimeout: v.timeout.Sub(time.Now()),
		&#125;,
	&#125;

	return resp
&#125;

func deleteKeyOperate(d *database, key interface&#123;&#125;) (resp dbResponse) &#123;
	// get dirty-map index and set read-lock
	index := d.hash(key) % d.size
	_map := d.data[index]
	_map.lock.RLock()
	defer _map.lock.RUnlock()

	if _, ok := _map.dirty[key]; !ok &#123;
		// condition: cannot find key in dirty-map
		// return value
		resp = dbResponse&#123;
			responseType: withValue,
			returns:      nil,
		&#125;
	&#125; else &#123;
		// delete key-value pair
		_map.lock.Lock()
		defer _map.lock.Unlock()
		delete(_map.dirty, key)

		// return value
		resp = dbResponse&#123;
			responseType: withValue,
			returns:      nil,
		&#125;
	&#125;

	return resp
&#125;

func rangeDbOperate(d *database, f func(key, value interface&#123;&#125;)) (resp dbResponse) &#123;
	for i := uint(0); i &lt; d.size; i++ &#123;
		// set write-lock
		current := d.data[i]
		current.lock.Lock()

		// execute function
		for k, v := range current.dirty &#123;
			f(k, v.data)
		&#125;
		current.lock.Unlock()
	&#125;

	// return value
	resp = dbResponse&#123;
		responseType: withValue,
		returns:      nil,
	&#125;

	return resp
&#125;

测试经测试，多go程的情况下，进行一千万次读写所用的总时间为8.6s，比上次进行的单go程性能更优

这篇博文是CeobeBot的框架CeobeBotFramework的kv部分的总结
总说redis单线程性能高，最近正好在实现自拟的框架，于是产生了一个用golang实现一个高性能kv的想法，进行了一天的coding、单元测试和基准测试以后，得出的结论是：

有锁的并发kv性能低于无锁的kv

思路先贴一下定义结构的代码：
type IDatabase interface &#123;
	Get(key interface&#123;&#125;) (value interface&#123;&#125;, timeout time.Duration, err error)
	Set(key interface&#123;&#125;, value interface&#123;&#125;, timeout time.Duration) (v interface&#123;&#125;, t time.Duration, e error)
	Delete(key interface&#123;&#125;) (ok bool, err error)
	Range(f func(key, value interface&#123;&#125;))
	Close()
&#125;

type database struct &#123;
	mu    sync.RWMutex
	buff  chan *dbRequest
	exit  chan struct&#123;&#125;
	dirty map[interface&#123;&#125;]*entity
&#125;

相信大家看到这里都已经知道了我的思路：就是用一个chanel作为kv的命令缓冲区，保证kv的并发安全性，然后使用go routine作为consumer消费这个chanel里的指令，至于那个锁mu sync.RWMutex呢，则是为进行并发读写的实现方式准备的
