实现首先要说明的是项目的结构：
.
|-- buff.go         // 缓冲chanel相关的结构体
|-- db.go           // kv主体实现
|-- db_test.go      // 测试文件
|-- entity.go       // kv实际存储内容
`-- error.go        // 自定义错误

db.gopackage kv

import (
	"sync"
	"time"
)

type IDatabase interface &#123;
	Get(key interface&#123;&#125;) (value interface&#123;&#125;, timeout time.Duration, err error)
	Set(key interface&#123;&#125;, value interface&#123;&#125;, timeout time.Duration) (v interface&#123;&#125;, t time.Duration, e error)
	Delete(key interface&#123;&#125;) (ok bool, err error)
	Range(f func(key, value interface&#123;&#125;))
	Close()
&#125;

func NewDatabase(workers, buffSize int) IDatabase &#123;
	db := &amp;database&#123;
		buff:  make(chan *dbRequest, buffSize),
		exit:  make(chan struct&#123;&#125;, 1),
		dirty: make(map[interface&#123;&#125;]*entity),
		mu:    sync.RWMutex&#123;&#125;,
	&#125;
	db.operator(workers)
	return db
&#125;

type database struct &#123;
	mu    sync.RWMutex
	buff  chan *dbRequest
	exit  chan struct&#123;&#125;
	dirty map[interface&#123;&#125;]*entity
&#125;

func (d database) Get(key interface&#123;&#125;) (value interface&#123;&#125;, timeout time.Duration, err error) &#123;
	req := newDbRequest(getValue)
	defer close(req.callback)
	req.args = map[requestFields]interface&#123;&#125;&#123;
		requestKey: key,
	&#125;
	d.buff &lt;- req

	resp := &lt;-req.callback
	switch resp.responseType &#123;
	case withError:
		err := resp.returns[responseError].(error)
		return nil, time.Duration(0), err
	default:
		value := resp.returns[responseValue]
		timeout := resp.returns[responseTimeout].(time.Time)
		out := timeout.Sub(time.Now())
		return value, out, nil
	&#125;
&#125;

func (d database) Set(key interface&#123;&#125;, value interface&#123;&#125;, timeout time.Duration) (v interface&#123;&#125;, t time.Duration, e error) &#123;
	req := newDbRequest(setValue)
	defer close(req.callback)
	req.args = map[requestFields]interface&#123;&#125;&#123;
		requestKey:     key,
		requestValue:   value,
		requestTimeout: time.Now().Add(timeout),
	&#125;
	d.buff &lt;- req

	resp := &lt;-req.callback
	switch resp.responseType &#123;
	case withError:
		err := resp.returns[responseError].(error)
		return nil, time.Duration(0), err
	default:
		value := resp.returns[responseValue]
		timeout := resp.returns[responseTimeout].(time.Time)
		out := timeout.Sub(time.Now())
		return value, out, nil
	&#125;
&#125;

func (d database) Delete(key interface&#123;&#125;) (ok bool, err error) &#123;
	req := newDbRequest(deleteKey)
	defer close(req.callback)
	req.args = map[requestFields]interface&#123;&#125;&#123;
		requestKey: key,
	&#125;
	d.buff &lt;- req

	resp := &lt;-req.callback
	switch resp.responseType &#123;
	case withError:
		err := resp.returns[responseError].(error)
		return false, err
	default:
		return true, nil
	&#125;
&#125;

func (d database) Range(f func(key interface&#123;&#125;, value interface&#123;&#125;)) &#123;
	req := newDbRequest(rangeDB)
	defer close(req.callback)
	req.args = map[requestFields]interface&#123;&#125;&#123;
		requestFunc: f,
	&#125;
	d.buff &lt;- req

	&lt;-req.callback
	return
&#125;

func (d *database) operator(workers int) &#123;
	if workers == 1 &#123;
		go func(d *database) &#123;
			for &#123;
				select &#123;
				case request := &lt;-d.buff:
					switch request.requestType &#123;
					case getValue:
						key := request.args[requestKey]
						if entity, ok := d.dirty[key]; !ok &#123;
							callback := dbResponse&#123;
								responseType: withError,
								returns: map[responseFields]interface&#123;&#125;&#123;
									responseError: notFoundError&#123;key: key&#125;,
								&#125;,
							&#125;
							request.callback &lt;- callback
						&#125; else &#123;
							if entity.timeout.Before(time.Now()) &#123;
								delete(d.dirty, key)
								callback := dbResponse&#123;
									responseType: withError,
									returns: map[responseFields]interface&#123;&#125;&#123;
										responseError: notFoundError&#123;key: key&#125;,
									&#125;,
								&#125;
								request.callback &lt;- callback
							&#125; else &#123;
								callback := dbResponse&#123;
									responseType: withValue,
									returns: map[responseFields]interface&#123;&#125;&#123;
										responseValue:   entity.data,
										responseTimeout: entity.timeout,
									&#125;,
								&#125;
								request.callback &lt;- callback
							&#125;
						&#125;
					case setValue:
						key := request.args[requestKey]
						value := request.args[requestValue]
						timeout := request.args[requestTimeout].(time.Time)
						entity := &amp;entity&#123;
							timeout: timeout,
							data:    value,
							mu:      sync.RWMutex&#123;&#125;,
						&#125;
						d.dirty[key] = entity

						callback := dbResponse&#123;
							responseType: withValue,
							returns: map[responseFields]interface&#123;&#125;&#123;
								responseValue:   entity.data,
								responseTimeout: entity.timeout,
							&#125;,
						&#125;
						request.callback &lt;- callback
					case deleteKey:
						key := request.args[requestKey]
						if _, ok := d.dirty[key]; ok &#123;
							delete(d.dirty, key)
							callback := dbResponse&#123;
								responseType: withValue,
								returns:      nil,
							&#125;
							request.callback &lt;- callback
						&#125; else &#123;
							callback := dbResponse&#123;
								responseType: withValue,
								returns:      nil,
							&#125;
							request.callback &lt;- callback
						&#125;
					case rangeDB:
						function := request.args[requestFunc].(func(key, value interface&#123;&#125;))
						for i, e := range d.dirty &#123;
							function(i, e.data)
						&#125;
						callback := dbResponse&#123;
							responseType: withValue,
							returns:      nil,
						&#125;
						request.callback &lt;- callback
					&#125;
				case &lt;-d.exit:
					return
				&#125;
			&#125;
		&#125;(d)
	&#125; else &#123;
		exitChan := make(chan struct&#123;&#125;, workers)
		for i := 0; i &lt; workers; i++ &#123;
			go func(e chan struct&#123;&#125;, d *database) &#123;
				for &#123;
					select &#123;
					case request := &lt;-d.buff:
						switch request.requestType &#123;
						case getValue:
							d.mu.RLock()
							key := request.args[requestKey]
							if entity, ok := d.dirty[key]; !ok &#123;
								callback := dbResponse&#123;
									responseType: withError,
									returns: map[responseFields]interface&#123;&#125;&#123;
										responseError: notFoundError&#123;key: key&#125;,
									&#125;,
								&#125;
								request.callback &lt;- callback
							&#125; else &#123;
								entity.read()
								if entity.timeout.Before(time.Now()) &#123;
									delete(d.dirty, key)
									callback := dbResponse&#123;
										responseType: withError,
										returns: map[responseFields]interface&#123;&#125;&#123;
											responseError: notFoundError&#123;key: key&#125;,
										&#125;,
									&#125;
									request.callback &lt;- callback
								&#125; else &#123;
									callback := dbResponse&#123;
										responseType: withValue,
										returns: map[responseFields]interface&#123;&#125;&#123;
											responseValue:   entity.data,
											responseTimeout: entity.timeout,
										&#125;,
									&#125;
									request.callback &lt;- callback
								&#125;
								entity.readDone()
							&#125;
							d.mu.RUnlock()
						case setValue:
							d.mu.Lock()
							key := request.args[requestKey]
							value := request.args[requestValue]
							timeout := request.args[requestTimeout].(time.Time)
							entity := &amp;entity&#123;
								timeout: timeout,
								data:    value,
								mu:      sync.RWMutex&#123;&#125;,
							&#125;
							entity.write()
							d.dirty[key] = entity

							callback := dbResponse&#123;
								responseType: withValue,
								returns: map[responseFields]interface&#123;&#125;&#123;
									responseValue:   entity.data,
									responseTimeout: entity.timeout,
								&#125;,
							&#125;
							entity.writeDone()
							request.callback &lt;- callback
							d.mu.Unlock()
						case deleteKey:
							d.mu.Lock()
							key := request.args[requestKey]
							if e, ok := d.dirty[key]; ok &#123;
								e.write()
								delete(d.dirty, key)
								callback := dbResponse&#123;
									responseType: withValue,
									returns:      nil,
								&#125;
								request.callback &lt;- callback
							&#125; else &#123;
								callback := dbResponse&#123;
									responseType: withValue,
									returns:      nil,
								&#125;
								request.callback &lt;- callback
							&#125;
							d.mu.Unlock()
						case rangeDB:
							d.mu.Lock()
							function := request.args[requestFunc].(func(key, value interface&#123;&#125;))
							for i, e := range d.dirty &#123;
								e.write()
								function(i, e.data)
								e.writeDone()
							&#125;
							callback := dbResponse&#123;
								responseType: withValue,
								returns:      nil,
							&#125;
							request.callback &lt;- callback
							d.mu.Unlock()
						&#125;
					case &lt;-exitChan:
						return
					&#125;
				&#125;
			&#125;(exitChan, d)
		&#125;
		go func(e chan struct&#123;&#125;, d *database, w int) &#123;
			select &#123;
			case &lt;-d.exit:
				for i := 0; i &lt; w; i++ &#123;
					e &lt;- struct&#123;&#125;&#123;&#125;
				&#125;
			&#125;
		&#125;(exitChan, d, workers)
	&#125;

&#125;

