    wg := &amp;sync.WaitGroup{}
    wg.Add(1)
	go Println(ch, wg)
	go Insert(100, ch, wg)
	wg.Wait()
}

func Insert(times int, ch chan struct{}, wg *sync.WaitGroup) {
    wg.Add(2*times)
    wg.Done()
    for i := 0; i &lt; times; {
		select {
		case ch &lt;- struct{}{}:
			i++
			wg.Done()
		default:
			println("wait for inserting...")
			time.Sleep(time.Millisecond)
		}
	}
}

