/**
 * @description: 方阵快速幂
 * @param {SquareMatrix} A 快速幂的底，即方阵A
 * @param {int} n 指数
 * @return {SquareMatrix} 计算结果，即A^n
 */
SquareMatrix matrix_quick_power(SquareMatrix A,int n){
    SquareMatrix base = A;
    SquareMatrix result;
    //对B进行初始化，初始化为单位阵
    for(int i = 0;i &lt; N;i++){
        for(int j = 0;j &lt; N;j++){
            if(i == j){
                result.matrix[i][j] = 1;
            }else{
                result.matrix[i][j] = 0;
            }
        }
    }
    while(n)
    {
        if(n&amp;1)
        {
            result = mul(result,base);
        }
        base = mul(base,base);
        n = n&gt;&gt;1;
    }
    return result;
}

