int Eratosthenes(int n, bool *is_prime, int *prime)
{
    int p = 0;
    for (int i = 0; i &lt;= n; ++i)
        is_prime[i] = 1;
    is_prime[0] = is_prime[1] = 0;
    for (int i = 2; i &lt;= n; ++i)
    {
        if (is_prime[i])
        {
            prime[p++] = i; // prime[p]æ˜¯i,åç½®è‡ªå¢è¿ç®—ä»£è¡¨å½“å‰ç´ æ•°æ•°é‡
            if ((long long)i * i &lt;= n)
