void printPermutation(int x, int n, bool *Visited, int *answer)
{
	if (x &gt; n)
	{
		for (int i = 0; i &lt; n; i++)
		{
			print(answer[i]);
		}
	}
	for (int i = 0; i &lt; n; i++)
	{
		if (!Visited[i])
		{
			answer[x-1] = i+1;
			Visited[i] = true;
			printPermutation(x+1, n, Visited, answer);
			Visited[i] = false;
		}
	}
}

